// Get the string data from the previous node's output field
const jsonString = $json.output;

// Parse the string into a real JSON object
const parsedData = JSON.parse(jsonString);

// Return the new object with its individual keys
return parsedData;

const items = $input.all();

// The .map() function now produces an array of objects.
const messages = items.filter((item) => {
  // Filter out irrelevant "channel_join" messages and bots you wish to ignore.
  return item.json.subtype !== "channel_join";
}).map((item) => {
  const messageData = item.json;

  // ‚ú® KEY CHANGE: Determine the role. If a bot_id exists, it's the 'assistant'. Otherwise, it's the 'user'.
  const role = messageData.bot_id ? 'assistant' : 'user';

  // 1. Get the raw user ID.
  const rawUserId = messageData.user || messageData.bot_id || 'unknown_user';

  // 2. Format the timestamp.
  const date = new Date(+messageData.ts * 1000);
  const ts = date.toISOString().slice(0, 19).replace('T', ' ');

  // 3. Extract and clean the message text (your existing logic is great).
  let cleanedText = '';
  if (messageData.blocks && messageData.blocks.length > 0) {
    for (const block of messageData.blocks) {
      if (block.type === 'rich_text') {
        for (const section of block.elements) {
          if (section.type === 'rich_text_section') {
            for (const element of section.elements) {
              if (element.type === 'text') {
                cleanedText += element.text;
              } else if (element.type === 'user') {
                cleanedText += `@${element.user_id}`;
              } else if (element.type === 'link') {
                /** @type {any} */
                const linkElem = element;
                const linkUrl = linkElem.url;
                const linkText = linkElem.text || linkUrl;
                cleanedText += `[${linkText}](${linkUrl})`;
              }
            }
          }
        }
      }
    }
  } else {
    cleanedText = messageData.text || '';
  }
  cleanedText = cleanedText.replace(/<@([A-Z0-9]+)>/g, (_m, u) => `@${u}`);

  // ‚ú® KEY CHANGE: Instead of a single string, create a content string.
  const content = `[${ts}] @${rawUserId}: ${cleanedText.trim()}`;

  // ‚ú® KEY CHANGE: Return a structured object for each message.
  return {
    role: role,
    content: content,
  };
});

// ‚ú® KEY CHANGE: Return the array of message objects directly.
return [{ json: { messages: messages } }];

